import React from 'react';
import './Banner.css';

function Banner() {
  return (
    <section className="banner">
      <div className="banner-overlay">
        <h1 className="banner-title">Tiệm Trà Nhà Bưởi</h1>
        <p className="banner-slogan">Nơi thư giãn, thưởng trà và gắn kết bạn bè!</p>
      </div>
    </section>
  );
}

export default Banner;
