import React from 'react';
import './About.css';

function About() {
  return (
    <section className="about">
      <h2>Về Tiệm Trà Nhà Bưởi</h2>
      <p>
        Tiệm Trà Nhà Bưởi là điểm đến lý tưởng cho những ai yêu thích không gian xôi động, gần gũi và thưởng thức các loại trà thơm ngon, đồ ăn vặt hấp dẫn. Chúng tôi mang đến trải nghiệm thư giãn, thân thiện, phù hợp cho bạn bè, gia đình và làm việc nhóm. Hiện tiệm có 2 chi nhánh thuận tiện cho bạn ghé thăm bất cứ lúc nào!
      </p>
    </section>
  );
}

export default About;
