import React from 'react';
import './About.css';

function About() {
  return (
    <section className="about">
      <h2>Về Chúng Tôi</h2>
      <p>
        Tiệm Trà Nhà Bưởi được thành lập với tình yêu và đam mê dành cho trà truyền thống Việt Nam. Chúng tôi tự hào mang đến những tách trà thượng hạng với hương vị đích thực, được chế biến từ những nguyên liệu tự nhiên và phương pháp truyền thống. Với không gian ấm cúng và dịch vụ tận tâm, chúng tôi mong muốn mang đến cho quý khách những khoảnh khắc thư giãn tuyệt vời bên tách trà thơm ngon.
      </p>
    </section>
  );
}

export default About;
